import './App.scss';
import React, {useState,useEffect} from 'react';
import Header from './components/Header';
import Attribution from './components/Attribution';
import ListContainer from './components/ListContainer';
import ToggleState from './components/ToggleState';



function App() {
  const [inputText, setInputText]=useState("");
  const [toDoList,setToDoList]= useState([]);
  const [status, setStatus]=useState("All")
  const [filteredTodos, setFilteredTodos]= useState([])




  const filterHandler = ()=>{


    switch(status){

      case "Completed":
        setFilteredTodos(toDoList.filter(toDo=>toDo.complete===true))
        break


        case "Active":
          setFilteredTodos(toDoList.filter(toDo=>toDo.complete!==true))
          break



        default:
          setFilteredTodos(toDoList)
        break
    }

  }

  useEffect(()=>{
    filterHandler();
     }, [toDoList,status])

  return (
    <div className="App">
<Header inputText={inputText} setInputText={setInputText} data={toDoList} updateData={setToDoList}/>
<ListContainer toDo={filteredTodos} setToDo={setToDoList}  setStatus={setStatus}/>
<ToggleState   setStatus={setStatus}/>


<Attribution/>
    </div>
  );
}

export default App;
