import React from "react";

const ToggleState=({setStatus})=>{


    const filterList = (e)=>{
        const filter =e.target.innerText;
        switch(filter){
            case "Completed":
                setStatus("Completed")
                break

                case "Active":
                setStatus("Active")
                break

                case "Clear Completed":
                setStatus("Clear Completed")
                break

                default:
                    setStatus("All")
                    break
        }

    }




    return (
        <div className="Toggles">
        <span className="filterBtn"
        onClick={filterList}
        >All</span>
        <span className="filterBtn"
        onClick={filterList}
        >
        Active</span>
        <span className="filterBtn"
        onClick={filterList}>
        Completed
        </span>
        </div>
    )
}

export default ToggleState;
