import React, { useState } from "react";

const DesktopFilters=({toDo, setToDo})=>{
    const [completed, setCompleted]=useState(false)
    const clearCompleted= ()=>{
            setToDo(toDo.filter(item=>item.complete!==true))
    }
    const showCompleted = ()=>{
       toDo.map(item=>{
        if(item.complete===true){
        console.log(item)
        }
        return item
       })
    }



    return(
        <div className="deskToggle item">
        {toDo.length===1?<span> 1 item left</span>:
        <span> {toDo.length} items left</span>}
        <span className="filterBtn">All</span>
        <span className="filterBtn">Active</span>
        <span className="filterBtn"
        onClick={showCompleted}
        >

        Completed</span>
        <span className="clearBtn"
        onClick={clearCompleted}
        >Clear Completed</span>
        </div>
    )
}

export default DesktopFilters
