import React from "react";

const DesktopFilters=({toDo, setToDo, setStatus})=>{


    const clearCompleted= ()=>{
            setToDo(toDo.filter(item=>item.complete!==true))
    }
    const filterList = (e)=>{
        const filter =e.target.innerText;
        if(filter==="Completed"){
            setStatus("Completed")
        }
        else if(filter==="Active"){
            setStatus("Active")
        }
        else{
            setStatus("All")
        }





    }



    return(
        <div className="deskToggle item">
        {toDo.length===1?<span> 1 item left</span>:
        <span> {toDo.length} items left</span>}
        <span className="filterBtn"
        onClick={filterList}
        >All</span>
        <span className="filterBtn"
        onClick={filterList}
        >
        Active</span>
        <span className="filterBtn"
        onClick={filterList}>
        Completed
        </span>
        <span className="clearBtn"
        onClick={clearCompleted}
        >Clear Completed</span>
        </div>
    )
}

export default DesktopFilters
