import React from "react";

const DesktopFilters=({toDo, setToDo, setStatus})=>{


    const filterList = (e)=>{
        const filter =e.target.innerText;
        console.log(filter);
        switch(filter){
            case "Completed":
                setStatus("Completed")
                break

                case "Active":
                setStatus("Active")
                break

                case "Clear Completed":
                setStatus("Clear Completed")
                break

                default:
                    setStatus("All")
                    break
        }


        if(filter==="Completed"){
            setStatus("Completed")
        }
        else if(filter==="Active"){
            setStatus("Active")
        }
        else{
            setStatus("All")
        }





    }



    return(
        <div className="deskToggle item">
        {toDo.length===1?<span> 1 item left</span>:
        <span> {toDo.length} items left</span>}
        <span className="filterBtn"
        onClick={filterList}
        >All</span>
        <span className="filterBtn"
        onClick={filterList}
        >
        Active</span>
        <span className="filterBtn"
        onClick={filterList}>
        Completed
        </span>
        <span className="clearBtn"
        onClick={filterList}
        >Clear Completed</span>
        </div>
    )
}

export default DesktopFilters
