import React, {useState} from 'react';
import {ReactComponent as ReactLogoMoon} from '../images/icon-moon.svg';
import {ReactComponent as ReactLogoSun} from '../images/icon-sun.svg';
import TaskInput from './TaskInput';
import desktopLight from '../images/bg-desktop-light.jpg'
import desktopDark from '../images/bg-desktop-dark.jpg'



const Header = ({inputText, setInputText, data,
updateData, darkMode, setDarkMode})=>{
    const [background,setBackground]= useState(desktopLight);

    const bgImage = {
        backgroundImage:`url(${background})`
      };



    return (
        <div className="header" style={bgImage}>
        <div className='heading'>
        <h1>to do</h1>
        <a href='null'
         className='.icon'
         onClick={(e)=>{
            e.preventDefault();
            if (darkMode===false){
                setDarkMode(true)
                setBackground(desktopDark)
            }
            else{
                setDarkMode(false)
                setBackground(desktopLight)
            }



            }

            }


            >
        {darkMode===false?<ReactLogoMoon/>:<ReactLogoSun/>}
        </a>
        </div>
            <TaskInput inputText={inputText}
            setInputText={setInputText}
             newItem={data}
             setNewItem={updateData}
             darkMode={darkMode}
             />
        </div>
    )
}

export default Header
